import Card from "../card/card";

const Hand = ({ monsters }) => {
  return (
    <div className="hand_container">
      {monsters.slice(0, 3).map((monster) => (
        <Card key={monsters.name} monster={monster} />
      ))}
    </div>
  );
};

export default Hand;
